import React from 'react';
import { 
    Container, 
    Grid, 
    Card, 
    CardContent, 
    CardActions, 
    Button, 
    Typography,
    makeStyles 
} from '@material-ui/core';
import { useHistory } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
    container: {
        paddingTop: theme.spacing(4),
        paddingBottom: theme.spacing(4),
    },
    card: {
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
    },
    cardContent: {
        flexGrow: 1,
    },
}));

const quizzes = [
    {
        id: 1,
        title: 'Basic Grammar Test',
        description: 'Test your basic English grammar knowledge',
        difficulty: 'Easy',
        time: 30,
        questions: 20
    },
    {
        id: 2,
        title: 'Intermediate Test',
        description: 'For intermediate English learners',
        difficulty: 'Medium',
        time: 45,
        questions: 30
    },
    {
        id: 3,
        title: 'Advanced Test',
        description: 'Challenge yourself with advanced English',
        difficulty: 'Hard',
        time: 60,
        questions: 40
    }
];

const QuizList = () => {
    const classes = useStyles();
    const history = useHistory();

    const handleStartQuiz = (quizId) => {
        history.push(`/quiz/${quizId}`);
    };

    return (
        <Container className={classes.container}>
            <Typography variant="h4" gutterBottom>
                Available Tests
            </Typography>
            <Grid container spacing={4}>
                {quizzes.map((quiz) => (
                    <Grid item key={quiz.id} xs={12} sm={6} md={4}>
                        <Card className={classes.card}>
                            <CardContent className={classes.cardContent}>
                                <Typography gutterBottom variant="h5" component="h2">
                                    {quiz.title}
                                </Typography>
                                <Typography>
                                    {quiz.description}
                                </Typography>
                                <Typography color="textSecondary">
                                    Difficulty: {quiz.difficulty}
                                </Typography>
                                <Typography color="textSecondary">
                                    Time: {quiz.time} minutes
                                </Typography>
                                <Typography color="textSecondary">
                                    Questions: {quiz.questions}
                                </Typography>
                            </CardContent>
                            <CardActions>
                                <Button 
                                    size="small" 
                                    color="primary"
                                    onClick={() => handleStartQuiz(quiz.id)}
                                >
                                    Start Test
                                </Button>
                            </CardActions>
                        </Card>
                    </Grid>
                ))}
            </Grid>
        </Container>
    );
};

export default QuizList;
