import React, { useState, useEffect } from 'react';
import { 
    Container, 
    Paper, 
    Typography, 
    TextField, 
    Button, 
    LinearProgress,
    Box,
    Card,
    CardContent,
    Grid,
    Chip,
    makeStyles 
} from '@material-ui/core';
import { 
    Timer as TimerIcon,
    NavigateNext,
    NavigateBefore,
    Check
} from '@material-ui/icons';
import { useParams, useHistory } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
    container: {
        paddingTop: theme.spacing(4),
        paddingBottom: theme.spacing(4),
    },
    card: {
        borderRadius: 15,
        boxShadow: '0 4px 20px 0 rgba(0,0,0,0.12)',
    },
    header: {
        background: theme.palette.primary.main,
        color: 'white',
        padding: theme.spacing(2),
        borderRadius: '15px 15px 0 0',
    },
    content: {
        padding: theme.spacing(3),
    },
    question: {
        marginBottom: theme.spacing(4),
        fontSize: '1.2rem',
        fontWeight: 500,
        color: theme.palette.text.primary,
    },
    answer: {
        marginBottom: theme.spacing(2),
        '& .MuiOutlinedInput-root': {
            borderRadius: 10,
            '&:hover fieldset': {
                borderColor: theme.palette.primary.main,
            },
        },
    },
    progress: {
        height: 10,
        borderRadius: 5,
        marginBottom: theme.spacing(3),
        backgroundColor: theme.palette.grey[200],
        '& .MuiLinearProgress-bar': {
            borderRadius: 5,
        },
    },
    timer: {
        display: 'flex',
        alignItems: 'center',
        gap: theme.spacing(1),
        color: theme.palette.primary.main,
        fontWeight: 'bold',
        fontSize: '1.1rem',
    },
    difficultyChip: {
        marginLeft: theme.spacing(2),
    },
    navigationButtons: {
        display: 'flex',
        justifyContent: 'space-between',
        marginTop: theme.spacing(4),
    },
    button: {
        borderRadius: 20,
        padding: '8px 24px',
        textTransform: 'none',
        fontWeight: 'bold',
    },
    submitButton: {
        background: theme.palette.success.main,
        color: 'white',
        '&:hover': {
            background: theme.palette.success.dark,
        },
    },
}));

// Mock data - sau này sẽ lấy từ API
const mockQuestions = [
    {
        id: 1,
        text: "The students _____ in the classroom right now.",
        blanks: [
            {
                id: 1,
                correctAnswer: "are studying"
            }
        ],
        difficulty: "easy",
        points: 1
    },
    {
        id: 2,
        text: "If I _____ rich, I _____ travel around the world.",
        blanks: [
            {
                id: 1,
                correctAnswer: "were"
            },
            {
                id: 2,
                correctAnswer: "would"
            }
        ],
        difficulty: "medium",
        points: 2
    },
    {
        id: 3,
        text: "She _____ to Paris three times _____ 2019.",
        blanks: [
            {
                id: 1,
                correctAnswer: "has been"
            },
            {
                id: 2,
                correctAnswer: "since"
            }
        ],
        difficulty: "hard",
        points: 3
    }
];

const Quiz = () => {
    const classes = useStyles();
    const { id } = useParams();
    const history = useHistory();
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [answers, setAnswers] = useState({});
    const [timeLeft, setTimeLeft] = useState(30 * 60); // 30 minutes in seconds

    useEffect(() => {
        const timer = setInterval(() => {
            setTimeLeft((prevTime) => {
                if (prevTime <= 1) {
                    clearInterval(timer);
                    handleSubmit();
                    return 0;
                }
                return prevTime - 1;
            });
        }, 1000);

        return () => clearInterval(timer);
    }, []);

    const handleAnswerChange = (questionId, blankId, value) => {
        setAnswers(prev => ({
            ...prev,
            [questionId]: {
                ...prev[questionId],
                [blankId]: value
            }
        }));
    };

    const handleNext = () => {
        if (currentQuestion < mockQuestions.length - 1) {
            setCurrentQuestion(prev => prev + 1);
        }
    };

    const handlePrevious = () => {
        if (currentQuestion > 0) {
            setCurrentQuestion(prev => prev - 1);
        }
    };

    const handleSubmit = () => {
        // Tạo object kết quả
        const results = {
            questions: mockQuestions,
            answers: answers,
            timeSpent: 1800 - timeLeft // Thời gian làm bài (giây)
        };

        // Chuyển hướng đến trang Result với dữ liệu
        history.push({
            pathname: '/result',
            state: { results }
        });
    };

    const formatTime = (seconds) => {
        const minutes = Math.floor(seconds / 60);
        const remainingSeconds = seconds % 60;
        return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
    };

    const question = mockQuestions[currentQuestion];
    const progress = ((currentQuestion + 1) / mockQuestions.length) * 100;

    return (
        <Container maxWidth="md" className={classes.container}>
            <Card className={classes.card}>
                <Box className={classes.header}>
                    <Grid container alignItems="center" justifyContent="space-between">
                        <Grid item>
                            <Typography variant="h5">
                                English Grammar Quiz
                            </Typography>
                        </Grid>
                        <Grid item>
                            <Box className={classes.timer}>
                                <TimerIcon />
                                {formatTime(timeLeft)}
                            </Box>
                        </Grid>
                    </Grid>
                </Box>

                <CardContent className={classes.content}>
                    <LinearProgress 
                        variant="determinate" 
                        value={progress} 
                        className={classes.progress}
                    />
                    
                    <Box display="flex" alignItems="center" marginBottom={3}>
                        <Typography variant="subtitle1">
                            Question {currentQuestion + 1} of {mockQuestions.length}
                        </Typography>
                        <Chip
                            label={question.difficulty.toUpperCase()}
                            color="primary"
                            size="small"
                            className={classes.difficultyChip}
                        />
                    </Box>

                    <Typography className={classes.question}>
                        {question.text}
                    </Typography>

                    <Grid container spacing={2}>
                        {question.blanks.map((blank, index) => (
                            <Grid item xs={12} key={blank.id}>
                                <TextField
                                    fullWidth
                                    label={`Answer ${index + 1}`}
                                    variant="outlined"
                                    className={classes.answer}
                                    value={answers[question.id]?.[blank.id] || ''}
                                    onChange={(e) => handleAnswerChange(question.id, blank.id, e.target.value)}
                                    placeholder="Type your answer here..."
                                />
                            </Grid>
                        ))}
                    </Grid>

                    <Box className={classes.navigationButtons}>
                        <Button
                            variant="contained"
                            onClick={handlePrevious}
                            disabled={currentQuestion === 0}
                            className={classes.button}
                            startIcon={<NavigateBefore />}
                        >
                            Previous
                        </Button>
                        {currentQuestion === mockQuestions.length - 1 ? (
                            <Button
                                variant="contained"
                                onClick={handleSubmit}
                                className={`${classes.button} ${classes.submitButton}`}
                                startIcon={<Check />}
                            >
                                Submit Quiz
                            </Button>
                        ) : (
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={handleNext}
                                className={classes.button}
                                endIcon={<NavigateNext />}
                            >
                                Next Question
                            </Button>
                        )}
                    </Box>
                </CardContent>
            </Card>
        </Container>
    );
};

export default Quiz;
