import React from 'react';
import { Typography, Paper, List, ListItem, ListItemText, makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
        padding: theme.spacing(3),
    },
    score: {
        marginBottom: theme.spacing(3),
    },
    difficulty: {
        marginTop: theme.spacing(2),
    },
}));

const Result = ({ questions, userAnswers }) => {
    const classes = useStyles();

    const calculateScoreByDifficulty = () => {
        const scores = {
            easy: { correct: 0, total: 0 },
            medium: { correct: 0, total: 0 },
            hard: { correct: 0, total: 0 }
        };

        questions.forEach((question, index) => {
            const isCorrect = question.blanks.every((blank, i) => 
                blank.correctAnswer.toLowerCase() === userAnswers[index][i].toLowerCase()
            );

            scores[question.difficulty].total += question.points;
            if (isCorrect) {
                scores[question.difficulty].correct += question.points;
            }
        });

        return scores;
    };

    const scores = calculateScoreByDifficulty();
    const totalScore = Object.values(scores).reduce((acc, curr) => acc + curr.correct, 0);
    const totalPossible = Object.values(scores).reduce((acc, curr) => acc + curr.total, 0);

    return (
        <Paper className={classes.root}>
            <Typography variant="h4" className={classes.score}>
                Final Score: {totalScore}/{totalPossible}
            </Typography>

            <Typography variant="h6" className={classes.difficulty}>
                Score by Difficulty:
            </Typography>
            <List>
                {Object.entries(scores).map(([difficulty, score]) => (
                    <ListItem key={difficulty}>
                        <ListItemText
                            primary={`${difficulty.charAt(0).toUpperCase() + difficulty.slice(1)}: ${score.correct}/${score.total}`}
                        />
                    </ListItem>
                ))}
            </List>
        </Paper>
    );
};

export default Result;
