import React, { useState, useEffect } from 'react';
import {
    Container,
    Card,
    CardContent,
    Typography,
    Box,
    Button,
    makeStyles,
    Divider,
    Chip,
    CircularProgress
} from '@material-ui/core';
import {
    CheckCircle as CorrectIcon,
    Cancel as WrongIcon,
    Timer as TimerIcon,
    EmojiEvents as TrophyIcon,
    Replay as RetryIcon,
    Home as HomeIcon
} from '@material-ui/icons';
import { useLocation, useHistory } from 'react-router-dom';
import axios from 'axios';

const useStyles = makeStyles((theme) => ({
    root: {
        minHeight: '100vh',
        backgroundColor: '#FFF8E1',
        paddingTop: theme.spacing(4),
        paddingBottom: theme.spacing(4),
    },
    card: {
        borderRadius: 16,
        boxShadow: '0 4px 20px rgba(183, 121, 31, 0.1)',
    },
    header: {
        background: 'linear-gradient(45deg, #D69E2E 30%, #ECC94B 90%)',
        color: 'white',
        padding: theme.spacing(3),
        borderRadius: '16px 16px 0 0',
        textAlign: 'center',
    },
    scoreContainer: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'column',
        padding: theme.spacing(4),
        backgroundColor: 'rgba(255, 248, 225, 0.5)',
    },
    score: {
        fontSize: '3rem',
        fontWeight: 'bold',
        color: '#B7791F',
        marginBottom: theme.spacing(1),
    },
    percentage: {
        fontSize: '1.5rem',
        color: '#975A16',
    },
    statsContainer: {
        display: 'flex',
        justifyContent: 'space-around',
        padding: theme.spacing(3),
        backgroundColor: 'white',
    },
    statItem: {
        textAlign: 'center',
    },
    statValue: {
        fontSize: '1.5rem',
        fontWeight: 'bold',
        color: '#744210',
    },
    statLabel: {
        color: '#975A16',
    },
    answersContainer: {
        padding: theme.spacing(3),
    },
    answerItem: {
        marginBottom: theme.spacing(3),
        padding: theme.spacing(2),
        backgroundColor: 'rgba(255, 248, 225, 0.3)',
        borderRadius: 8,
    },
    correctAnswer: {
        color: '#2F855A',
        display: 'flex',
        alignItems: 'center',
        '& .MuiSvgIcon-root': {
            marginRight: theme.spacing(1),
        },
    },
    wrongAnswer: {
        color: '#C53030',
        display: 'flex',
        alignItems: 'center',
        '& .MuiSvgIcon-root': {
            marginRight: theme.spacing(1),
        },
    },
    buttonContainer: {
        display: 'flex',
        justifyContent: 'center',
        gap: theme.spacing(2),
        padding: theme.spacing(3),
    },
    button: {
        padding: '12px 24px',
        borderRadius: 25,
        '&.retry': {
            background: 'linear-gradient(45deg, #D69E2E 30%, #ECC94B 90%)',
            color: 'white',
        },
    },
}));

const Result = () => {
    const classes = useStyles();
    const location = useLocation();
    const history = useHistory();
    const [saving, setSaving] = useState(true);
    const [saveError, setSaveError] = useState(null);
    
    const calculateScore = (results) => {
        let correct = 0;
        results.quiz.blanks.forEach(blank => {
            if (results.answers[blank.id] === blank.correctAnswer) {
                correct++;
            }
        });
        return correct;
    };
    
    useEffect(() => {
        const saveResult = async () => {
            if (!location.state || !location.state.results) return;
            
            const { results } = location.state;
            const score = calculateScore(results);
            const totalQuestions = results.quiz.blanks.length;
            const percentage = Math.round((score / totalQuestions) * 100);

            try {
                await axios.post('http://localhost:5000/api/results', {
                    userId: 'guest',
                    questionId: results.quiz.question_id,
                    answers: results.answers,
                    score,
                    totalQuestions,
                    percentage,
                    timeSpent: results.timeSpent
                });
                setSaving(false);
            } catch (error) {
                console.error('Error saving result:', error);
                setSaveError('Failed to save result');
                setSaving(false);
            }
        };

        saveResult();
    }, [location.state]);

    if (!location.state || !location.state.results) {
        history.push('/');
        return null;
    }

    const { results } = location.state;

    if (!results.quiz || !results.quiz.blanks) {
        history.push('/');
        return null;
    }

    const score = calculateScore(results);
    const totalQuestions = results.quiz.blanks.length;
    const percentage = Math.round((score / totalQuestions) * 100);
    const timeSpent = results.timeSpent;

    const formatTime = (seconds) => {
        const minutes = Math.floor(seconds / 60);
        const remainingSeconds = seconds % 60;
        return `${minutes}m ${remainingSeconds}s`;
    };

    return (
        <Box className={classes.root}>
            <Container maxWidth="md">
                <Card className={classes.card}>
                    <Box className={classes.header}>
                        <TrophyIcon style={{ fontSize: 60, marginBottom: 16 }} />
                        <Typography variant="h4" gutterBottom>
                            {results.quiz.title}
                        </Typography>
                    </Box>

                    {saving ? (
                        <Box display="flex" justifyContent="center" p={2}>
                            <CircularProgress size={24} style={{ color: '#D69E2E' }} />
                        </Box>
                    ) : saveError ? (
                        <Box display="flex" justifyContent="center" p={2}>
                            <Typography color="error">{saveError}</Typography>
                        </Box>
                    ) : null}

                    <Box className={classes.scoreContainer}>
                        <Typography className={classes.score}>
                            {score}/{totalQuestions}
                        </Typography>
                        <Typography className={classes.percentage}>
                            {percentage}%
                        </Typography>
                    </Box>

                    <Box className={classes.statsContainer}>
                        <Box className={classes.statItem}>
                            <Typography className={classes.statValue}>
                                {score}
                            </Typography>
                            <Typography className={classes.statLabel}>
                                Correct
                            </Typography>
                        </Box>
                        <Box className={classes.statItem}>
                            <Typography className={classes.statValue}>
                                {totalQuestions - score}
                            </Typography>
                            <Typography className={classes.statLabel}>
                                Wrong
                            </Typography>
                        </Box>
                        <Box className={classes.statItem}>
                            <Typography className={classes.statValue}>
                                {formatTime(timeSpent)}
                            </Typography>
                            <Typography className={classes.statLabel}>
                                Time Spent
                            </Typography>
                        </Box>
                    </Box>

                    <Box className={classes.answersContainer}>
                        <Typography variant="h6" gutterBottom>
                            Detailed Review
                        </Typography>
                        {results.quiz.blanks.map((blank, index) => (
                            <Box key={blank.id} className={classes.answerItem}>
                                <Typography variant="subtitle1" gutterBottom>
                                    Blank {index + 1}
                                </Typography>
                                <Box mt={1}>
                                    {results.answers[blank.id] === blank.correctAnswer ? (
                                        <Typography className={classes.correctAnswer}>
                                            <CorrectIcon />
                                            Correct: {blank.correctAnswer}
                                        </Typography>
                                    ) : (
                                        <>
                                            <Typography className={classes.wrongAnswer}>
                                                <WrongIcon />
                                                Your answer: {results.answers[blank.id] || 'No answer'}
                                            </Typography>
                                            <Typography className={classes.correctAnswer} style={{ marginTop: 8 }}>
                                                <CorrectIcon />
                                                Correct answer: {blank.correctAnswer}
                                            </Typography>
                                        </>
                                    )}
                                </Box>
                            </Box>
                        ))}
                    </Box>

                    <Box className={classes.buttonContainer}>
                        <Button
                            variant="outlined"
                            className={classes.button}
                            startIcon={<HomeIcon />}
                            onClick={() => history.push('/')}
                        >
                            Back to List
                        </Button>
                        <Button
                            variant="contained"
                            className={`${classes.button} retry`}
                            startIcon={<RetryIcon />}
                            onClick={() => history.push(`/quiz/${results.quiz.question_id}`)}
                        >
                            Try Again
                        </Button>
                    </Box>
                </Card>
            </Container>
        </Box>
    );
};

export default Result;
