import React, { useState, useEffect } from 'react';
import { 
    Container, 
    Grid, 
    Card, 
    CardContent, 
    CardActions, 
    Button, 
    Typography,
    Box,
    Chip,
    makeStyles,
    CircularProgress 
} from '@material-ui/core';
import { 
    Timer as TimerIcon,
    School as SchoolIcon,
    Star as StarIcon,
    PlayArrow as StartIcon
} from '@material-ui/icons';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
    root: {
        minHeight: '100vh',
        backgroundColor: '#FFF8E1', // Light yellow background
        paddingTop: theme.spacing(4),
        paddingBottom: theme.spacing(4),
    },
    header: {
        textAlign: 'center',
        marginBottom: theme.spacing(6),
    },
    headerTitle: {
        color: '#B7791F', // Dark yellow
        fontWeight: 700,
        marginBottom: theme.spacing(2),
    },
    card: {
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        borderRadius: 16,
        transition: 'transform 0.2s ease-in-out',
        backgroundColor: 'white',
        boxShadow: '0 4px 20px rgba(183, 121, 31, 0.1)', // Yellow-tinted shadow
        '&:hover': {
            transform: 'translateY(-5px)',
            boxShadow: '0 8px 25px rgba(183, 121, 31, 0.2)',
        },
    },
    cardContent: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },
    cardTitle: {
        color: '#744210', // Darker yellow
        fontWeight: 600,
        marginBottom: theme.spacing(2),
    },
    cardDescription: {
        color: '#666',
        marginBottom: theme.spacing(2),
    },
    infoContainer: {
        display: 'flex',
        alignItems: 'center',
        marginBottom: theme.spacing(1),
        color: '#975A16', // Yellow-brown
    },
    icon: {
        marginRight: theme.spacing(1),
        color: '#D69E2E', // Golden yellow
    },
    difficultyChip: {
        backgroundColor: '#FEFCBF', // Very light yellow
        color: '#975A16',
        fontWeight: 600,
        marginBottom: theme.spacing(2),
    },
    startButton: {
        background: 'linear-gradient(45deg, #D69E2E 30%, #ECC94B 90%)',
        color: 'white',
        padding: '10px 25px',
        borderRadius: 25,
        fontWeight: 600,
        textTransform: 'none',
        boxShadow: '0 3px 15px rgba(214, 158, 46, 0.3)',
        '&:hover': {
            background: 'linear-gradient(45deg, #B7791F 30%, #D69E2E 90%)',
        },
    },
    cardActions: {
        padding: theme.spacing(2),
        borderTop: '1px solid #FFF8E1',
    }
}));

const QuizList = () => {
    const classes = useStyles();
    const history = useHistory();
    const [quizzes, setQuizzes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchQuizzes = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/quizzes');
                setQuizzes(response.data);
                setLoading(false);
            } catch (err) {
                setError('Failed to load quizzes');
                setLoading(false);
            }
        };

        fetchQuizzes();
    }, []);

    const handleStartQuiz = (quizId) => {
        history.push(`/quiz/${quizId}`);
    };

    if (loading) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                <CircularProgress />
            </Box>
        );
    }

    if (error) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                <Typography color="error">{error}</Typography>
            </Box>
        );
    }

    return (
        <Box className={classes.root}>
            <Container>
                <Box className={classes.header}>
                    <Typography variant="h3" className={classes.headerTitle}>
                        English Grammar Quizzes
                    </Typography>
                    <Typography variant="h6" color="textSecondary">
                        Test your English skills with our interactive quizzes
                    </Typography>
                </Box>

                <Grid container spacing={4}>
                    {quizzes.map((quiz) => (
                        <Grid item key={quiz._id} xs={12} sm={6} md={4}>
                            <Card className={classes.card}>
                                <CardContent className={classes.cardContent}>
                                    <Chip
                                        label={quiz.difficulty.toUpperCase()}
                                        className={classes.difficultyChip}
                                        size="small"
                                    />
                                    <Typography variant="h5" className={classes.cardTitle}>
                                        {quiz.title}
                                    </Typography>
                                    <Typography className={classes.cardDescription}>
                                        {quiz.description}
                                    </Typography>
                                    
                                    <Box className={classes.infoContainer}>
                                        <TimerIcon className={classes.icon} />
                                        <Typography>{quiz.duration} minutes</Typography>
                                    </Box>
                                    
                                    <Box className={classes.infoContainer}>
                                        <SchoolIcon className={classes.icon} />
                                        <Typography>{quiz.total_questions} questions</Typography>
                                    </Box>
                                    
                                    <Box className={classes.infoContainer}>
                                        <StarIcon className={classes.icon} />
                                        <Typography>{quiz.total_points} points</Typography>
                                    </Box>

                                    <Box className={classes.infoContainer}>
                                        <Typography variant="subtitle2" color="textSecondary">
                                            Passing Score: {quiz.passing_score} points
                                        </Typography>
                                    </Box>
                                </CardContent>
                                
                                <CardActions className={classes.cardActions}>
                                    <Button
                                        fullWidth
                                        className={classes.startButton}
                                        startIcon={<StartIcon />}
                                        onClick={() => handleStartQuiz(quiz.quiz_id)}
                                    >
                                        Start Quiz
                                    </Button>
                                </CardActions>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            </Container>
        </Box>
    );
};

export default QuizList;
