import React from 'react';
import { 
    Container, 
    Grid, 
    Card, 
    CardContent, 
    CardActions, 
    Button, 
    Typography,
    Box,
    Chip,
    makeStyles 
} from '@material-ui/core';
import { 
    Timer as TimerIcon,
    School as SchoolIcon,
    Star as StarIcon,
    PlayArrow as StartIcon
} from '@material-ui/icons';
import { useHistory } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
    root: {
        minHeight: '100vh',
        backgroundColor: '#FFF8E1', // Light yellow background
        paddingTop: theme.spacing(4),
        paddingBottom: theme.spacing(4),
    },
    header: {
        textAlign: 'center',
        marginBottom: theme.spacing(6),
    },
    headerTitle: {
        color: '#B7791F', // Dark yellow
        fontWeight: 700,
        marginBottom: theme.spacing(2),
    },
    card: {
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        borderRadius: 16,
        transition: 'transform 0.2s ease-in-out',
        backgroundColor: 'white',
        boxShadow: '0 4px 20px rgba(183, 121, 31, 0.1)', // Yellow-tinted shadow
        '&:hover': {
            transform: 'translateY(-5px)',
            boxShadow: '0 8px 25px rgba(183, 121, 31, 0.2)',
        },
    },
    cardContent: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },
    cardTitle: {
        color: '#744210', // Darker yellow
        fontWeight: 600,
        marginBottom: theme.spacing(2),
    },
    cardDescription: {
        color: '#666',
        marginBottom: theme.spacing(2),
    },
    infoContainer: {
        display: 'flex',
        alignItems: 'center',
        marginBottom: theme.spacing(1),
        color: '#975A16', // Yellow-brown
    },
    icon: {
        marginRight: theme.spacing(1),
        color: '#D69E2E', // Golden yellow
    },
    difficultyChip: {
        backgroundColor: '#FEFCBF', // Very light yellow
        color: '#975A16',
        fontWeight: 600,
        marginBottom: theme.spacing(2),
    },
    startButton: {
        background: 'linear-gradient(45deg, #D69E2E 30%, #ECC94B 90%)',
        color: 'white',
        padding: '10px 25px',
        borderRadius: 25,
        fontWeight: 600,
        textTransform: 'none',
        boxShadow: '0 3px 15px rgba(214, 158, 46, 0.3)',
        '&:hover': {
            background: 'linear-gradient(45deg, #B7791F 30%, #D69E2E 90%)',
        },
    },
    cardActions: {
        padding: theme.spacing(2),
        borderTop: '1px solid #FFF8E1',
    }
}));

const quizzes = [
    {
        id: 1,
        title: 'Basic Grammar Test',
        description: 'Perfect for beginners! Test your fundamental English grammar knowledge with this comprehensive quiz.',
        difficulty: 'Easy',
        time: 30,
        questions: 20,
        points: 100
    },
    {
        id: 2,
        title: 'Intermediate Grammar Challenge',
        description: 'Take your English skills to the next level with our intermediate grammar challenge.',
        difficulty: 'Medium',
        time: 45,
        questions: 30,
        points: 150
    },
    {
        id: 3,
        title: 'Advanced English Mastery',
        description: 'Push your limits with complex grammar structures and advanced English concepts.',
        difficulty: 'Hard',
        time: 60,
        questions: 40,
        points: 200
    }
];

const QuizList = () => {
    const classes = useStyles();
    const history = useHistory();

    const handleStartQuiz = (quizId) => {
        history.push(`/quiz/${quizId}`);
    };

    return (
        <Box className={classes.root}>
            <Container>
                <Box className={classes.header}>
                    <Typography variant="h3" className={classes.headerTitle}>
                        English Grammar Quizzes
                    </Typography>
                    <Typography variant="h6" color="textSecondary">
                        Test your English skills with our interactive quizzes
                    </Typography>
                </Box>

                <Grid container spacing={4}>
                    {quizzes.map((quiz) => (
                        <Grid item key={quiz.id} xs={12} sm={6} md={4}>
                            <Card className={classes.card}>
                                <CardContent className={classes.cardContent}>
                                    <Chip
                                        label={quiz.difficulty}
                                        className={classes.difficultyChip}
                                        size="small"
                                    />
                                    <Typography variant="h5" className={classes.cardTitle}>
                                        {quiz.title}
                                    </Typography>
                                    <Typography className={classes.cardDescription}>
                                        {quiz.description}
                                    </Typography>
                                    
                                    <Box className={classes.infoContainer}>
                                        <TimerIcon className={classes.icon} />
                                        <Typography>{quiz.time} minutes</Typography>
                                    </Box>
                                    
                                    <Box className={classes.infoContainer}>
                                        <SchoolIcon className={classes.icon} />
                                        <Typography>{quiz.questions} questions</Typography>
                                    </Box>
                                    
                                    <Box className={classes.infoContainer}>
                                        <StarIcon className={classes.icon} />
                                        <Typography>{quiz.points} points</Typography>
                                    </Box>
                                </CardContent>
                                
                                <CardActions className={classes.cardActions}>
                                    <Button
                                        fullWidth
                                        className={classes.startButton}
                                        startIcon={<StartIcon />}
                                        onClick={() => handleStartQuiz(quiz.id)}
                                    >
                                        Start Quiz
                                    </Button>
                                </CardActions>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            </Container>
        </Box>
    );
};

export default QuizList;
