import React from 'react';
import { 
  Typography, 
  Box, 
  List, 
  ListItem, 
  ListItemText 
} from '@material-ui/core';

const Result = ({ score, totalQuestions, questions, userAnswers }) => {
  const percentage = (score / totalQuestions) * 100;

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        Quiz Results
      </Typography>

      <Typography variant="h5" gutterBottom>
        Score: {score}/{totalQuestions} ({percentage.toFixed(1)}%)
      </Typography>

      <Box mt={3}>
        <Typography variant="h6">Review Your Answers:</Typography>
        <List>
          {questions.map((question, index) => (
            <ListItem key={index}>
              <ListItemText
                primary={`Question ${index + 1}`}
                secondary={
                  <>
                    <Typography component="span" variant="body2">
                      Your answers: {userAnswers[index].join(', ')}
                    </Typography>
                    <br />
                    <Typography component="span" variant="body2">
                      Correct answer: {question.blanks.map(blank => blank.correctAnswer).join(', ')}
                    </Typography>
                  </>
                }
              />
            </ListItem>
          ))}
        </List>
      </Box>
    </div>
  );
};

export default Result;
