import React, { useState, useEffect } from 'react';
import { 
    Container, 
    Card, 
    Typography,
    Box,
    Button,
    LinearProgress,
    Chip,
    makeStyles,
    CircularProgress,
    Paper,
    IconButton,
    Tooltip,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions
} from '@material-ui/core';
import {
    Timer as TimerIcon,
    Help as HelpIcon,
    Flag as FlagIcon,
    Save as SaveIcon,
    ExitToApp as ExitIcon,
    Check as CheckIcon,
    Bookmark as BookmarkIcon,
    NavigateNext as NextIcon,
    NavigateBefore as PrevIcon
} from '@material-ui/icons';
import { useParams, useHistory } from 'react-router-dom';
import axios from 'axios';
import { motion, AnimatePresence } from 'framer-motion';

const useStyles = makeStyles((theme) => ({
    root: {
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #FFF8E1 0%, #FFFAF0 100%)',
        paddingTop: theme.spacing(4),
        paddingBottom: theme.spacing(4),
    },
    quizContainer: {
        position: 'relative',
        maxWidth: 1000,
        margin: '0 auto',
    },
    header: {
        position: 'sticky',
        top: 0,
        zIndex: 100,
        background: 'rgba(255, 255, 255, 0.9)',
        backdropFilter: 'blur(10px)',
        borderRadius: '20px',
        padding: theme.spacing(2, 3),
        marginBottom: theme.spacing(3),
        boxShadow: '0 4px 20px rgba(0,0,0,0.05)',
    },
    headerContent: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
    },
    timerContainer: {
        display: 'flex',
        alignItems: 'center',
        gap: theme.spacing(1),
        color: '#D69E2E',
        '& .MuiSvgIcon-root': {
            fontSize: 20,
        },
    },
    progress: {
        height: 6,
        borderRadius: 3,
        marginTop: theme.spacing(2),
        backgroundColor: 'rgba(214, 158, 46, 0.1)',
        '& .MuiLinearProgress-bar': {
            backgroundColor: '#D69E2E',
        },
    },
    mainCard: {
        borderRadius: 20,
        boxShadow: '0 4px 20px rgba(0,0,0,0.05)',
        overflow: 'visible',
    },
    readingSection: {
        padding: theme.spacing(4),
        backgroundColor: 'white',
        borderRadius: '20px 20px 0 0',
        position: 'relative',
    },
    readingText: {
        fontSize: '1.1rem',
        lineHeight: 1.8,
        color: '#2D3748',
        '& .blank-input': {
            display: 'inline-block',
            width: '180px',
            margin: '0 8px',
            padding: '4px 12px',
            borderRadius: '15px',
            border: '2px solid #ECC94B',
            backgroundColor: '#FFFBEB',
            color: '#744210',
            fontWeight: 500,
            transition: 'all 0.3s ease',
            cursor: 'pointer',
            textAlign: 'center',
            '&:hover': {
                backgroundColor: '#FEF3C7',
                borderColor: '#D69E2E',
            },
            '&.selected': {
                backgroundColor: '#FEF3C7',
                borderColor: '#D69E2E',
                boxShadow: '0 0 0 3px rgba(214, 158, 46, 0.2)',
            },
        },
    },
    optionsSection: {
        padding: theme.spacing(4),
        backgroundColor: '#FFFBEB',
        borderRadius: '0 0 20px 20px',
        borderTop: '2px solid rgba(214, 158, 46, 0.1)',
    },
    optionsTitle: {
        color: '#975A16',
        fontWeight: 600,
        marginBottom: theme.spacing(2),
    },
    optionsGrid: {
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
        gap: theme.spacing(2),
        marginBottom: theme.spacing(3),
    },
    optionChip: {
        width: '100%',
        height: 'auto',
        padding: '10px',
        borderRadius: '15px',
        backgroundColor: 'white',
        border: '2px solid transparent',
        transition: 'all 0.3s ease',
        '&:hover': {
            backgroundColor: '#FEF3C7',
            borderColor: '#D69E2E',
        },
        '&.selected': {
            backgroundColor: '#FEF3C7',
            borderColor: '#D69E2E',
            color: '#744210',
            fontWeight: 600,
        },
    },
    navigationBar: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginTop: theme.spacing(4),
        padding: theme.spacing(2),
        backgroundColor: 'white',
        borderRadius: '20px',
        boxShadow: '0 4px 20px rgba(0,0,0,0.05)',
    },
    navButton: {
        padding: '10px 24px',
        borderRadius: '15px',
        textTransform: 'none',
        fontWeight: 600,
        '&.submit': {
            background: 'linear-gradient(45deg, #D69E2E 30%, #ECC94B 90%)',
            color: 'white',
            boxShadow: '0 4px 14px rgba(214, 158, 46, 0.4)',
        },
    },
    helpButton: {
        position: 'fixed',
        right: theme.spacing(3),
        bottom: theme.spacing(3),
        backgroundColor: '#D69E2E',
        color: 'white',
        '&:hover': {
            backgroundColor: '#B7791F',
        },
    },
}));

const Quiz = () => {
    const classes = useStyles();
    const { id } = useParams();
    const history = useHistory();
    const [quiz, setQuiz] = useState(null);
    const [answers, setAnswers] = useState({});
    const [selectedBlank, setSelectedBlank] = useState(null);
    const [loading, setLoading] = useState(true);
    const [timeLeft, setTimeLeft] = useState(0);
    const [helpOpen, setHelpOpen] = useState(false);

    useEffect(() => {
        const fetchQuiz = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/questions/${id}`);
                setQuiz(response.data);
                setTimeLeft(response.data.blanks.length * 120); // 2 minutes per blank
                setLoading(false);
            } catch (error) {
                console.error('Error fetching quiz:', error);
                setLoading(false);
            }
        };
        fetchQuiz();
    }, [id]);

    useEffect(() => {
        if (timeLeft <= 0) return;

        const timer = setInterval(() => {
            setTimeLeft(prev => prev - 1);
        }, 1000);

        return () => clearInterval(timer);
    }, [timeLeft]);

    const handleBlankClick = (blankId) => {
        setSelectedBlank(blankId);
    };

    const handleOptionClick = (option) => {
        if (selectedBlank !== null) {
            setAnswers(prev => ({
                ...prev,
                [selectedBlank]: option
            }));
            setSelectedBlank(null);
        }
    };

    const handleSubmit = () => {
        const results = {
            quiz: quiz,
            answers: answers,
            timeSpent: quiz.blanks.length * 120 - timeLeft
        };
        history.push('/result', { results });
    };

    if (loading) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                <CircularProgress style={{ color: '#D69E2E' }} />
            </Box>
        );
    }

    return (
        <Box className={classes.root}>
            <Container className={classes.quizContainer}>
                <Paper className={classes.header}>
                    <Box className={classes.headerContent}>
                        <Typography variant="h5" style={{ fontWeight: 600, color: '#2D3748' }}>
                            {quiz.title}
                        </Typography>
                        <Box className={classes.timerContainer}>
                            <TimerIcon />
                            <Typography variant="h6">
                                {Math.floor(timeLeft / 60)}:{(timeLeft % 60).toString().padStart(2, '0')}
                            </Typography>
                        </Box>
                    </Box>
                    <LinearProgress 
                        variant="determinate" 
                        value={(timeLeft / (quiz.blanks.length * 120)) * 100}
                        className={classes.progress}
                    />
                </Paper>

                <Card className={classes.mainCard}>
                    <Box className={classes.readingSection}>
                        <Typography className={classes.readingText}>
                            {quiz.question_text.split('____').map((part, index, array) => (
                                <React.Fragment key={index}>
                                    {part}
                                    {index < array.length - 1 && (
                                        <motion.span
                                            className={`blank-input ${selectedBlank === quiz.blanks[index].id ? 'selected' : ''}`}
                                            onClick={() => handleBlankClick(quiz.blanks[index].id)}
                                            whileHover={{ scale: 1.02 }}
                                            whileTap={{ scale: 0.98 }}
                                        >
                                            {answers[quiz.blanks[index].id] || '...'}
                                        </motion.span>
                                    )}
                                </React.Fragment>
                            ))}
                        </Typography>
                    </Box>

                    <Box className={classes.optionsSection}>
                        <Typography variant="h6" className={classes.optionsTitle}>
                            {selectedBlank ? 'Select an option:' : 'Click any blank to fill'}
                        </Typography>
                        {selectedBlank && (
                            <motion.div 
                                className={classes.optionsGrid}
                                initial={{ opacity: 0, y: 20 }}
                                animate={{ opacity: 1, y: 0 }}
                            >
                                {quiz.blanks.find(b => b.id === selectedBlank)?.options.map((option) => (
                                    <Chip
                                        key={option}
                                        label={option}
                                        onClick={() => handleOptionClick(option)}
                                        className={`${classes.optionChip} ${
                                            answers[selectedBlank] === option ? 'selected' : ''
                                        }`}
                                    />
                                ))}
                            </motion.div>
                        )}
                    </Box>
                </Card>

                <Box className={classes.navigationBar}>
                    <Button
                        variant="outlined"
                        color="primary"
                        className={classes.navButton}
                        onClick={() => history.push('/')}
                        startIcon={<ExitIcon />}
                    >
                        Exit Quiz
                    </Button>
                    <Button
                        variant="contained"
                        className={`${classes.navButton} submit`}
                        onClick={handleSubmit}
                        endIcon={<CheckIcon />}
                        disabled={Object.keys(answers).length < quiz.blanks.length}
                    >
                        Submit Answers
                    </Button>
                </Box>

                <Tooltip title="Need help?" arrow>
                    <IconButton 
                        className={classes.helpButton}
                        onClick={() => setHelpOpen(true)}
                    >
                        <HelpIcon />
                    </IconButton>
                </Tooltip>

                <Dialog 
                    open={helpOpen} 
                    onClose={() => setHelpOpen(false)}
                    maxWidth="sm"
                    fullWidth
                >
                    <DialogTitle>How to Take the Quiz</DialogTitle>
                    <DialogContent>
                        <Typography paragraph>
                            1. Click on any blank space in the text to fill it
                        </Typography>
                        <Typography paragraph>
                            2. Select the appropriate word from the options below
                        </Typography>
                        <Typography paragraph>
                            3. Complete all blanks before submitting
                        </Typography>
                        <Typography>
                            4. Watch the timer and manage your time wisely
                        </Typography>
                    </DialogContent>
                    <DialogActions>
                        <Button 
                            onClick={() => setHelpOpen(false)} 
                            color="primary"
                        >
                            Got it!
                        </Button>
                    </DialogActions>
                </Dialog>
            </Container>
        </Box>
    );
};

export default Quiz;
