import React, { useState, useEffect } from 'react';
import { 
    Container, 
    Card, 
    CardContent, 
    Typography,
    Box,
    TextField,
    Button,
    LinearProgress,
    Chip,
    makeStyles,
    CircularProgress,
    Divider
} from '@material-ui/core';
import {
    Timer as TimerIcon,
    NavigateNext,
    NavigateBefore,
    Check,
    Save as SaveIcon
} from '@material-ui/icons';
import { useParams, useHistory } from 'react-router-dom';
import axios from 'axios';

const useStyles = makeStyles((theme) => ({
    root: {
        minHeight: '100vh',
        backgroundColor: '#FFF8E1',
        paddingTop: theme.spacing(4),
        paddingBottom: theme.spacing(4),
    },
    card: {
        borderRadius: 16,
        boxShadow: '0 4px 20px rgba(183, 121, 31, 0.1)',
    },
    header: {
        background: 'linear-gradient(45deg, #D69E2E 30%, #ECC94B 90%)',
        color: 'white',
        padding: theme.spacing(3),
        borderRadius: '16px 16px 0 0',
    },
    content: {
        padding: theme.spacing(3),
    },
    readingText: {
        fontSize: '1.1rem',
        lineHeight: 1.8,
        color: '#2D3748',
        marginBottom: theme.spacing(4),
        whiteSpace: 'pre-line',
    },
    blankField: {
        display: 'inline-block',
        width: '200px',
        margin: '0 8px',
        '& .MuiOutlinedInput-root': {
            backgroundColor: 'white',
            '&:hover fieldset': {
                borderColor: '#D69E2E',
            },
            '&.Mui-focused fieldset': {
                borderColor: '#B7791F',
            },
        },
    },
    timer: {
        display: 'flex',
        alignItems: 'center',
        color: 'white',
        fontSize: '1.2rem',
        '& .MuiSvgIcon-root': {
            marginRight: theme.spacing(1),
        },
    },
    progress: {
        height: 10,
        borderRadius: 5,
        backgroundColor: 'rgba(255, 255, 255, 0.2)',
        '& .MuiLinearProgress-bar': {
            backgroundColor: 'white',
        },
        marginTop: theme.spacing(2),
    },
    optionsContainer: {
        display: 'flex',
        flexWrap: 'wrap',
        gap: theme.spacing(1),
        marginBottom: theme.spacing(3),
    },
    optionChip: {
        backgroundColor: 'white',
        color: '#744210',
        '&:hover': {
            backgroundColor: '#FEFCBF',
        },
        '&.selected': {
            backgroundColor: '#D69E2E',
            color: 'white',
        },
    },
    buttonContainer: {
        display: 'flex',
        justifyContent: 'space-between',
        marginTop: theme.spacing(4),
    },
    button: {
        padding: '12px 24px',
        borderRadius: 25,
        '&.submit': {
            background: 'linear-gradient(45deg, #D69E2E 30%, #ECC94B 90%)',
            color: 'white',
        },
    },
}));

const Quiz = () => {
    const classes = useStyles();
    const { id } = useParams();
    const history = useHistory();
    const [quiz, setQuiz] = useState(null);
    const [answers, setAnswers] = useState({});
    const [loading, setLoading] = useState(true);
    const [timeLeft, setTimeLeft] = useState(0);

    useEffect(() => {
        const fetchQuiz = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/questions/${id}`);
                setQuiz(response.data);
                setTimeLeft(response.data.blanks.length * 120); // 2 minutes per blank
                setLoading(false);
            } catch (error) {
                console.error('Error fetching quiz:', error);
                setLoading(false);
            }
        };
        fetchQuiz();
    }, [id]);

    useEffect(() => {
        if (timeLeft <= 0) return;

        const timer = setInterval(() => {
            setTimeLeft(prev => prev - 1);
        }, 1000);

        return () => clearInterval(timer);
    }, [timeLeft]);

    const handleAnswerChange = (blankId, value) => {
        setAnswers(prev => ({
            ...prev,
            [blankId]: value
        }));
    };

    const handleSubmit = async () => {
        try {
            const results = questions.map(question => {
                const userAnswer = currentAnswers[question._id] || '';
                const isCorrect = question.correct_answer.toLowerCase() === userAnswer.toLowerCase();
                return { questionId: question._id, isCorrect };
            });

            const correctAnswers = results.filter(r => r.isCorrect).length;
            const scorePercentage = (correctAnswers / questions.length) * 100;

            setScore(scorePercentage);
            setSubmitted(true);

            // Lưu kết quả vào database
            const token = localStorage.getItem('token');
            await axios.post('http://localhost:5000/api/results', {
                userId: localStorage.getItem('userId'),
                quizType: 'fill-in-blanks',
                score: scorePercentage,
                answers: currentAnswers,
                totalQuestions: questions.length,
                timeTaken: Math.floor((Date.now() - startTime) / 60000) // Thời gian làm bài tính bằng phút
            }, {
                headers: { Authorization: `Bearer ${token}` }
            });

            // Chuyển hướng đến trang Profile sau 3 giây
            setTimeout(() => {
                history.push('/profile');
            }, 3000);

        } catch (error) {
            console.error('Error submitting answers:', error);
        }
    };

    if (loading) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                <CircularProgress style={{ color: '#D69E2E' }} />
            </Box>
        );
    }

    if (!quiz) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                <Typography color="error">Quiz not found</Typography>
            </Box>
        );
    }

    const formatTime = (seconds) => {
        const minutes = Math.floor(seconds / 60);
        const remainingSeconds = seconds % 60;
        return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
    };

    return (
        <Box className={classes.root}>
            <Container maxWidth="lg">
                <Card className={classes.card}>
                    <Box className={classes.header}>
                        <Typography variant="h5" gutterBottom>
                            {quiz.title}
                        </Typography>
                        <Box className={classes.timer}>
                            <TimerIcon />
                            {formatTime(timeLeft)}
                        </Box>
                        <LinearProgress 
                            variant="determinate" 
                            value={(timeLeft / (quiz.blanks.length * 120)) * 100}
                            className={classes.progress}
                        />
                    </Box>

                    <CardContent className={classes.content}>
                        <Box className={classes.readingText}>
                            {quiz.question_text.split('____').map((part, index, array) => (
                                <React.Fragment key={index}>
                                    {part}
                                    {index < array.length - 1 && (
                                        <TextField
                                            value={answers[quiz.blanks[index].id] || ''}
                                            onClick={() => handleBlankClick(quiz.blanks[index].id)}
                                            className={classes.blankInput}
                                            variant="outlined"
                                            size="small"
                                            disabled
                                        />
                                    )}
                                </React.Fragment>
                            ))}
                        </Box>

                        {quiz.blanks.map((blank, index) => (
                            <Box key={blank.id} mb={4}>
                                <Typography variant="subtitle1" gutterBottom>
                                    Blank {index + 1} Options:
                                </Typography>
                                <Box className={classes.optionsContainer}>
                                    {blank.options.map((option) => (
                                        <Chip
                                            key={option}
                                            label={option}
                                            className={`${classes.optionChip} ${
                                                answers[blank.id] === option ? 'selected' : ''
                                            }`}
                                            onClick={() => handleAnswerChange(blank.id, option)}
                                            clickable
                                        />
                                    ))}
                                </Box>
                                <Divider />
                            </Box>
                        ))}

                        <Box className={classes.buttonContainer}>
                            <Button
                                variant="outlined"
                                color="primary"
                                className={classes.button}
                                onClick={() => history.push('/')}
                            >
                                Back to List
                            </Button>
                            <Button
                                variant="contained"
                                className={`${classes.button} submit`}
                                onClick={handleSubmit}
                                startIcon={<Check />}
                            >
                                Submit Answers
                            </Button>
                        </Box>
                    </CardContent>
                </Card>
            </Container>
        </Box>
    );
};

export default Quiz;
