import React, { useState, useEffect } from 'react';
import { 
    Container, 
    Grid, 
    Card, 
    CardContent, 
    CardActions, 
    Button, 
    Typography,
    Box,
    Chip,
    makeStyles,
    CircularProgress,
    Divider 
} from '@material-ui/core';
import { 
    Assignment as QuizIcon,
    School as SchoolIcon,
    Star as StarIcon,
    PlayArrow as StartIcon
} from '@material-ui/icons';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
    root: {
        minHeight: '100vh',
        backgroundColor: '#FFF8E1', // Light yellow background
        paddingTop: theme.spacing(4),
        paddingBottom: theme.spacing(4),
    },
    header: {
        textAlign: 'center',
        marginBottom: theme.spacing(6),
    },
    headerTitle: {
        color: '#B7791F', // Dark yellow
        fontWeight: 700,
        marginBottom: theme.spacing(2),
    },
    card: {
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        borderRadius: 16,
        transition: 'transform 0.2s ease-in-out',
        backgroundColor: 'white',
        boxShadow: '0 4px 20px rgba(183, 121, 31, 0.1)', // Yellow-tinted shadow
        '&:hover': {
            transform: 'translateY(-5px)',
            boxShadow: '0 8px 25px rgba(183, 121, 31, 0.2)',
        },
    },
    cardContent: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },
    cardTitle: {
        color: '#744210', // Darker yellow
        fontWeight: 600,
        marginBottom: theme.spacing(2),
    },
    cardDescription: {
        color: '#666',
        marginBottom: theme.spacing(2),
    },
    infoContainer: {
        display: 'flex',
        alignItems: 'center',
        marginBottom: theme.spacing(1),
        color: '#975A16', // Yellow-brown
    },
    icon: {
        marginRight: theme.spacing(1),
        color: '#D69E2E', // Golden yellow
    },
    difficultyChip: {
        backgroundColor: '#FEFCBF', // Very light yellow
        color: '#975A16',
        fontWeight: 600,
        marginBottom: theme.spacing(2),
    },
    startButton: {
        background: 'linear-gradient(45deg, #D69E2E 30%, #ECC94B 90%)',
        color: 'white',
        padding: '10px 25px',
        borderRadius: 25,
        fontWeight: 600,
        textTransform: 'none',
        boxShadow: '0 3px 15px rgba(214, 158, 46, 0.3)',
        '&:hover': {
            background: 'linear-gradient(45deg, #B7791F 30%, #D69E2E 90%)',
        },
    },
    cardActions: {
        padding: theme.spacing(2),
        borderTop: '1px solid #FFF8E1',
    },
    categoryChip: {
        backgroundColor: '#FEFCBF',
        color: '#975A16',
        margin: theme.spacing(0, 1, 1, 0),
    },
    questionText: {
        color: '#744210',
        marginBottom: theme.spacing(2),
        display: '-webkit-box',
        '-webkit-line-clamp': 2,
        '-webkit-box-orient': 'vertical',
        overflow: 'hidden',
    },
    optionsContainer: {
        marginTop: theme.spacing(2),
    },
    option: {
        marginBottom: theme.spacing(1),
        color: '#666',
    }
}));

const QuizList = () => {
    const classes = useStyles();
    const history = useHistory();
    const [questions, setQuestions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/questions');
                setQuestions(response.data);
                setLoading(false);
            } catch (err) {
                setError('Failed to load questions');
                setLoading(false);
                console.error('Error:', err);
            }
        };

        fetchQuestions();
    }, []);

    const handleStartQuiz = (questionId) => {
        history.push(`/quiz/${questionId}`);
    };

    if (loading) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                <CircularProgress />
            </Box>
        );
    }

    if (error) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                <Typography color="error">{error}</Typography>
            </Box>
        );
    }

    return (
        <Box className={classes.root}>
            <Container>
                <Box className={classes.header}>
                    <Typography variant="h3" className={classes.headerTitle}>
                        English Practice Questions
                    </Typography>
                    <Typography variant="h6" color="textSecondary">
                        Improve your English with our interactive questions
                    </Typography>
                </Box>

                <Grid container spacing={4}>
                    {questions.map((question) => (
                        <Grid item key={question._id} xs={12} sm={6} md={4}>
                            <Card className={classes.card}>
                                <CardContent className={classes.cardContent}>
                                    <Box display="flex" flexWrap="wrap" mb={2}>
                                        <Chip
                                            label={question.difficulty.toUpperCase()}
                                            className={classes.difficultyChip}
                                            size="small"
                                        />
                                        <Chip
                                            label={question.category}
                                            className={classes.categoryChip}
                                            size="small"
                                        />
                                    </Box>

                                    <Typography variant="h6" className={classes.questionText}>
                                        {question.question_text || question.text}
                                    </Typography>

                                    {question.options && (
                                        <Box className={classes.optionsContainer}>
                                            <Typography variant="subtitle2" color="textSecondary" gutterBottom>
                                                Options:
                                            </Typography>
                                            {question.options.map((option, index) => (
                                                <Typography key={index} className={classes.option}>
                                                    â€¢ {option}
                                                </Typography>
                                            ))}
                                        </Box>
                                    )}

                                    <Divider className={classes.divider} />

                                    <Box className={classes.infoContainer}>
                                        <SchoolIcon className={classes.icon} />
                                        <Typography>Type: {question.type}</Typography>
                                    </Box>

                                    <Box className={classes.infoContainer}>
                                        <StarIcon className={classes.icon} />
                                        <Typography>Difficulty: {question.difficulty}</Typography>
                                    </Box>
                                </CardContent>
                                
                                <CardActions className={classes.cardActions}>
                                    <Button
                                        fullWidth
                                        className={classes.startButton}
                                        startIcon={<StartIcon />}
                                        onClick={() => handleStartQuiz(question.question_id)}
                                    >
                                        Practice Now
                                    </Button>
                                </CardActions>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            </Container>
        </Box>
    );
};

export default QuizList;
