import React, { useState, useEffect } from 'react';
import { 
    Container, 
    Card, 
    CardContent, 
    Typography,
    Box,
    TextField,
    Button,
    LinearProgress,
    Chip,
    makeStyles,
    CircularProgress,
    Divider
} from '@material-ui/core';
import {
    Timer as TimerIcon,
    NavigateNext,
    NavigateBefore,
    Check,
    Save as SaveIcon
} from '@material-ui/icons';
import { useParams, useHistory } from 'react-router-dom';
import axios from 'axios';

const useStyles = makeStyles((theme) => ({
    root: {
        minHeight: '100vh',
        backgroundColor: '#FFF8E1',
        paddingTop: theme.spacing(4),
        paddingBottom: theme.spacing(4),
    },
    card: {
        borderRadius: 16,
        boxShadow: '0 4px 20px rgba(183, 121, 31, 0.1)',
    },
    header: {
        background: 'linear-gradient(45deg, #D69E2E 30%, #ECC94B 90%)',
        color: 'white',
        padding: theme.spacing(3),
        borderRadius: '16px 16px 0 0',
    },
    content: {
        padding: theme.spacing(3),
    },
    readingText: {
        fontSize: '1.1rem',
        lineHeight: 1.8,
        color: '#2D3748',
        marginBottom: theme.spacing(4),
        whiteSpace: 'pre-line',
    },
    blankField: {
        display: 'inline-block',
        width: '200px',
        margin: '0 8px',
        '& .MuiOutlinedInput-root': {
            backgroundColor: 'white',
            '&:hover fieldset': {
                borderColor: '#D69E2E',
            },
            '&.Mui-focused fieldset': {
                borderColor: '#B7791F',
            },
        },
    },
    timer: {
        display: 'flex',
        alignItems: 'center',
        color: 'white',
        fontSize: '1.2rem',
        '& .MuiSvgIcon-root': {
            marginRight: theme.spacing(1),
        },
    },
    progress: {
        height: 10,
        borderRadius: 5,
        backgroundColor: 'rgba(255, 255, 255, 0.2)',
        '& .MuiLinearProgress-bar': {
            backgroundColor: 'white',
        },
        marginTop: theme.spacing(2),
    },
    optionsContainer: {
        display: 'flex',
        flexWrap: 'wrap',
        gap: theme.spacing(1),
        marginBottom: theme.spacing(3),
    },
    optionChip: {
        backgroundColor: 'white',
        color: '#744210',
        '&:hover': {
            backgroundColor: '#FEFCBF',
        },
        '&.selected': {
            backgroundColor: '#D69E2E',
            color: 'white',
        },
    },
    buttonContainer: {
        display: 'flex',
        justifyContent: 'space-between',
        marginTop: theme.spacing(4),
    },
    button: {
        padding: '12px 24px',
        borderRadius: 25,
        '&.submit': {
            background: 'linear-gradient(45deg, #D69E2E 30%, #ECC94B 90%)',
            color: 'white',
        },
    },
    quizContainer: {
        display: 'flex',
        gap: theme.spacing(4),
        padding: theme.spacing(2),
    },
    questionSection: {
        flex: '2',
        position: 'sticky',
        top: theme.spacing(2),
    },
    answersSection: {
        flex: '1',
        maxHeight: 'calc(100vh - 200px)', // Adjust based on your header/footer
        overflowY: 'auto',
        position: 'sticky',
        top: theme.spacing(2),
        backgroundColor: 'white',
        borderRadius: theme.spacing(1),
        padding: theme.spacing(2),
        boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    },
    answerItem: {
        marginBottom: theme.spacing(2),
        padding: theme.spacing(2),
        border: '1px solid #eee',
        borderRadius: theme.spacing(1),
        '&:hover': {
            backgroundColor: '#f5f5f5',
            cursor: 'pointer',
        },
    },
    answerTitle: {
        fontWeight: 'bold',
        marginBottom: theme.spacing(1),
    },
    answerOptions: {
        display: 'flex',
        flexDirection: 'column',
        gap: theme.spacing(1),
    },
    selectedAnswer: {
        backgroundColor: '#FEF3C7',
        borderColor: '#D69E2E',
    },
    blankInput: {
        position: 'relative',
        '& .MuiOutlinedInput-root': {
            paddingLeft: '32px', // Space for number
        }
    },
    blankNumber: {
        position: 'absolute',
        left: '8px',
        top: '50%',
        transform: 'translateY(-50%)',
        zIndex: 1,
        fontSize: '0.875rem',
        color: '#666',
        fontWeight: 'bold',
    },
    submitButton: {
        '&.Mui-disabled': {
            backgroundColor: '#ccc',
            color: '#666',
        }
    }
}));

const Quiz = () => {
    const classes = useStyles();
    const { id } = useParams();
    const history = useHistory();
    const [quiz, setQuiz] = useState(null);
    const [answers, setAnswers] = useState({});
    const [loading, setLoading] = useState(true);
    const [timeLeft, setTimeLeft] = useState(0);

    useEffect(() => {
        const fetchQuiz = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/questions/${id}`);
                setQuiz(response.data);
                setTimeLeft(response.data.blanks.length * 120); // 2 minutes per blank
                setLoading(false);
            } catch (error) {
                console.error('Error fetching quiz:', error);
                setLoading(false);
            }
        };
        fetchQuiz();
    }, [id]);

    useEffect(() => {
        if (timeLeft <= 0) return;

        const timer = setInterval(() => {
            setTimeLeft(prev => prev - 1);
        }, 1000);

        return () => clearInterval(timer);
    }, [timeLeft]);

    const handleAnswerChange = (blankId, value) => {
        setAnswers(prev => ({
            ...prev,
            [blankId]: value
        }));
    };

    const handleSubmit = () => {
        const results = {
            quiz: quiz,
            answers: answers,
            timeSpent: quiz.blanks.length * 120 - timeLeft
        };
        history.push('/result', { results });
    };

    // Check if all blanks are filled
    const isAllAnswered = () => {
        return quiz.blanks.every(blank => answers[blank.id]);
    };

    if (loading) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                <CircularProgress style={{ color: '#D69E2E' }} />
            </Box>
        );
    }

    if (!quiz) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                <Typography color="error">Quiz not found</Typography>
            </Box>
        );
    }

    const formatTime = (seconds) => {
        const minutes = Math.floor(seconds / 60);
        const remainingSeconds = seconds % 60;
        return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
    };

    return (
        <Box className={classes.root}>
            <Container maxWidth="lg">
                <Card className={classes.card}>
                    <Box className={classes.header}>
                        <Typography variant="h5" gutterBottom>
                            {quiz.title}
                        </Typography>
                        <Box className={classes.timer}>
                            <TimerIcon />
                            {formatTime(timeLeft)}
                        </Box>
                        <LinearProgress 
                            variant="determinate" 
                            value={(timeLeft / (quiz.blanks.length * 120)) * 100}
                            className={classes.progress}
                        />
                    </Box>

                    <CardContent>
                        <div className={classes.quizContainer}>
                            {/* Question Section */}
                            <div className={classes.questionSection}>
                                <Box className={classes.readingText}>
                                    {quiz.question_text.split('____').map((part, index, array) => (
                                        <React.Fragment key={index}>
                                            {part}
                                            {index < array.length - 1 && (
                                                <div className={classes.blankInput}>
                                                    <span className={classes.blankNumber}>{index + 1}</span>
                                                    <TextField
                                                        value={answers[quiz.blanks[index].id] || ''}
                                                        className={classes.blankInput}
                                                        variant="outlined"
                                                        size="small"
                                                        disabled
                                                    />
                                                </div>
                                            )}
                                        </React.Fragment>
                                    ))}
                                </Box>
                            </div>

                            {/* Scrollable Answers Section */}
                            <div className={classes.answersSection}>
                                <Typography variant="h6" gutterBottom>
                                    Answer Options
                                </Typography>
                                {quiz.blanks.map((blank, index) => (
                                    <div 
                                        key={blank.id}
                                        className={`${classes.answerItem} ${
                                            answers[blank.id] ? classes.selectedAnswer : ''
                                        }`}
                                    >
                                        <div className={classes.answerTitle}>
                                            Blank {index + 1}
                                        </div>
                                        <div className={classes.answerOptions}>
                                            {blank.options.map((option) => (
                                                <Chip
                                                    key={option}
                                                    label={option}
                                                    onClick={() => handleAnswerChange(blank.id, option)}
                                                    className={`${classes.optionChip} ${
                                                        answers[blank.id] === option ? 'selected' : ''
                                                    }`}
                                                    clickable
                                                />
                                            ))}
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>

                        <Box className={classes.buttonContainer}>
                            <Button
                                variant="outlined"
                                color="primary"
                                className={classes.button}
                                onClick={() => history.push('/')}
                            >
                                Back to List
                            </Button>
                            <Button
                                variant="contained"
                                className={`${classes.button} ${classes.submitButton} submit`}
                                onClick={handleSubmit}
                                startIcon={<Check />}
                                disabled={!isAllAnswered()}
                            >
                                Submit Answers
                            </Button>
                        </Box>
                    </CardContent>
                </Card>
            </Container>
        </Box>
    );
};

export default Quiz;
