import React, { useState, useEffect } from 'react';
import { 
    Container, 
    Grid, 
    Card, 
    CardContent, 
    Button, 
    Typography,
    Box,
    Chip,
    makeStyles,
    CircularProgress,
    Avatar,
    IconButton,
    InputBase,
    Paper
} from '@material-ui/core';
import { 
    Timer as TimerIcon,
    School as SchoolIcon,
    Star as StarIcon,
    Search as SearchIcon,
    FilterList as FilterIcon,
    Assignment as QuizIcon,
    ArrowForward as ArrowIcon
} from '@material-ui/icons';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
    root: {
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #FFF8E1 0%, #FFFAF0 100%)',
        paddingTop: theme.spacing(4),
        paddingBottom: theme.spacing(8),
    },
    header: {
        textAlign: 'center',
        marginBottom: theme.spacing(6),
        padding: theme.spacing(4),
        background: 'linear-gradient(45deg, #D69E2E 30%, #ECC94B 90%)',
        borderRadius: '0 0 50% 50% / 20px',
        boxShadow: '0 4px 20px rgba(183, 121, 31, 0.2)',
    },
    headerTitle: {
        color: 'white',
        fontWeight: 800,
        textShadow: '2px 2px 4px rgba(0,0,0,0.1)',
        marginBottom: theme.spacing(1),
    },
    headerSubtitle: {
        color: 'rgba(255,255,255,0.9)',
        fontWeight: 500,
    },
    searchBar: {
        position: 'relative',
        maxWidth: 600,
        margin: '-27px auto 40px',
        borderRadius: 50,
        backgroundColor: 'white',
        padding: '8px 24px',
        display: 'flex',
        alignItems: 'center',
        boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
    },
    searchInput: {
        marginLeft: theme.spacing(2),
        flex: 1,
    },
    filterButton: {
        marginLeft: theme.spacing(1),
    },
    card: {
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        borderRadius: 20,
        transition: 'all 0.3s ease',
        background: 'white',
        boxShadow: '0 4px 20px rgba(0,0,0,0.05)',
        '&:hover': {
            transform: 'translateY(-8px)',
            boxShadow: '0 12px 30px rgba(0,0,0,0.1)',
        },
    },
    cardMedia: {
        position: 'relative',
        backgroundColor: '#FFF8E1',
        height: 140,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: '20px 20px 0 0',
    },
    quizIcon: {
        width: 60,
        height: 60,
        backgroundColor: '#D69E2E',
        color: 'white',
    },
    cardContent: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },
    title: {
        fontWeight: 700,
        color: '#2D3748',
        marginBottom: theme.spacing(2),
        fontSize: '1.25rem',
    },
    description: {
        color: '#718096',
        marginBottom: theme.spacing(2),
    },
    chipContainer: {
        display: 'flex',
        gap: theme.spacing(1),
        marginBottom: theme.spacing(2),
    },
    difficultyChip: {
        backgroundColor: '#FEFCBF',
        color: '#975A16',
        fontWeight: 600,
    },
    categoryChip: {
        backgroundColor: '#FFF3C4',
        color: '#975A16',
        fontWeight: 600,
    },
    infoContainer: {
        display: 'flex',
        alignItems: 'center',
        color: '#718096',
        marginBottom: theme.spacing(1),
        '& svg': {
            marginRight: theme.spacing(1),
            color: '#D69E2E',
        },
    },
    startButton: {
        marginTop: 'auto',
        padding: '10px 24px',
        borderRadius: 50,
        background: 'linear-gradient(45deg, #D69E2E 30%, #ECC94B 90%)',
        color: 'white',
        fontWeight: 600,
        textTransform: 'none',
        fontSize: '1rem',
        boxShadow: '0 4px 14px rgba(214, 158, 46, 0.4)',
        '&:hover': {
            background: 'linear-gradient(45deg, #C47F1C 30%, #D6A53B 90%)',
        },
    },
    buttonIcon: {
        marginLeft: theme.spacing(1),
    },
}));

const QuizList = () => {
    const classes = useStyles();
    const history = useHistory();
    const [questions, setQuestions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/questions');
                setQuestions(response.data);
                setLoading(false);
            } catch (err) {
                console.error('Error:', err);
                setLoading(false);
            }
        };
        fetchQuestions();
    }, []);

    const handleStartQuiz = (questionId) => {
        history.replace(`/quiz/${questionId}`);
    };

    const filteredQuestions = questions.filter(question =>
        question.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        question.category?.toLowerCase().includes(searchTerm.toLowerCase())
    );

    if (loading) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                <CircularProgress style={{ color: '#D69E2E' }} />
            </Box>
        );
    }

    return (
        <Box className={classes.root}>
            <Box className={classes.header}>
                <Typography variant="h3" className={classes.headerTitle}>
                    PTE Reading Practice
                </Typography>
                <Typography variant="h6" className={classes.headerSubtitle}>
                    Master Fill in the Blanks with Interactive Exercises
                </Typography>
            </Box>

            <Container maxWidth="lg">
                <Paper component="form" className={classes.searchBar}>
                    <SearchIcon style={{ color: '#718096' }} />
                    <InputBase
                        className={classes.searchInput}
                        placeholder="Search by title or category..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    <IconButton className={classes.filterButton}>
                        <FilterIcon />
                    </IconButton>
                </Paper>

                <Grid container spacing={4}>
                    {filteredQuestions.map((question) => (
                        <Grid item key={question._id} xs={12} sm={6} md={4}>
                            <Card className={classes.card}>
                                <Box className={classes.cardMedia}>
                                    <Avatar className={classes.quizIcon}>
                                        <QuizIcon />
                                    </Avatar>
                                </Box>
                                
                                <CardContent className={classes.cardContent}>
                                    <Box className={classes.chipContainer}>
                                        <Chip
                                            label={question.difficulty.toUpperCase()}
                                            className={classes.difficultyChip}
                                            size="small"
                                        />
                                        <Chip
                                            label={question.category.replace(/_/g, ' ')}
                                            className={classes.categoryChip}
                                            size="small"
                                        />
                                    </Box>

                                    <Typography className={classes.title}>
                                        {question.title || `Reading ${question.question_id}`}
                                    </Typography>

                                    <Box className={classes.infoContainer}>
                                        <TimerIcon />
                                        <Typography>
                                            {question.blanks?.length * 2 || 10} minutes
                                        </Typography>
                                    </Box>

                                    <Box className={classes.infoContainer}>
                                        <SchoolIcon />
                                        <Typography>
                                            {question.blanks?.length || 0} questions
                                        </Typography>
                                    </Box>

                                    <Box className={classes.infoContainer}>
                                        <StarIcon />
                                        <Typography>
                                            {question.points || question.blanks?.length || 0} points
                                        </Typography>
                                    </Box>

                                    <Button
                                        fullWidth
                                        className={classes.startButton}
                                        onClick={() => handleStartQuiz(question.question_id)}
                                        endIcon={<ArrowIcon className={classes.buttonIcon} />}
                                    >
                                        Start Practice
                                    </Button>
                                </CardContent>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            </Container>
        </Box>
    );
};

export default QuizList;
