import React, { useState, useEffect } from 'react';
import { 
    Container, 
    Grid, 
    Card, 
    CardContent, 
    CardActions, 
    Button, 
    Typography,
    Box,
    Chip,
    makeStyles,
    CircularProgress,
    Divider 
} from '@material-ui/core';
import { 
    MenuBook as ReadingIcon,
    School as SchoolIcon,
    Timer as TimerIcon,
    PlayArrow as StartIcon
} from '@material-ui/icons';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
    root: {
        minHeight: '100vh',
        backgroundColor: '#FFF8E1',
        paddingTop: theme.spacing(4),
        paddingBottom: theme.spacing(4),
    },
    header: {
        textAlign: 'center',
        marginBottom: theme.spacing(6),
    },
    headerTitle: {
        color: '#B7791F',
        fontWeight: 700,
        marginBottom: theme.spacing(2),
    },
    card: {
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        borderRadius: 16,
        transition: 'transform 0.2s ease-in-out',
        backgroundColor: 'white',
        boxShadow: '0 4px 20px rgba(183, 121, 31, 0.1)',
        '&:hover': {
            transform: 'translateY(-5px)',
            boxShadow: '0 8px 25px rgba(183, 121, 31, 0.2)',
        },
    },
    cardContent: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },
    cardTitle: {
        color: '#744210',
        fontWeight: 600,
        marginBottom: theme.spacing(2),
    },
    difficultyChip: {
        backgroundColor: '#FEFCBF',
        color: '#975A16',
        fontWeight: 600,
        marginRight: theme.spacing(1),
        marginBottom: theme.spacing(2),
    },
    categoryChip: {
        backgroundColor: '#FFF3C4',
        color: '#975A16',
        fontWeight: 600,
        marginBottom: theme.spacing(2),
    },
    infoContainer: {
        display: 'flex',
        alignItems: 'center',
        marginBottom: theme.spacing(1),
        color: '#975A16',
    },
    icon: {
        marginRight: theme.spacing(1),
        color: '#D69E2E',
    },
    startButton: {
        background: 'linear-gradient(45deg, #D69E2E 30%, #ECC94B 90%)',
        color: 'white',
        padding: '12px 24px',
        borderRadius: 25,
        '&:hover': {
            background: 'linear-gradient(45deg, #C47F1C 30%, #D6A53B 90%)',
        },
    }
}));

const QuizList = () => {
    const classes = useStyles();
    const history = useHistory();
    const [questions, setQuestions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/questions');
                setQuestions(response.data);
                setLoading(false);
            } catch (err) {
                setError('Failed to load questions');
                setLoading(false);
            }
        };

        fetchQuestions();
    }, []);

    const handleStartQuiz = (questionId) => {
        history.push(`/quiz/${questionId}`);
    };

    if (loading) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                <CircularProgress style={{ color: '#D69E2E' }} />
            </Box>
        );
    }

    if (error) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                <Typography color="error">{error}</Typography>
            </Box>
        );
    }

    return (
        <Box className={classes.root}>
            <Container>
                <Box className={classes.header}>
                    <Typography variant="h3" className={classes.headerTitle}>
                        PTE Reading Practice
                    </Typography>
                    <Typography variant="h6" color="textSecondary">
                        Fill in the Blanks Exercises
                    </Typography>
                </Box>

                <Grid container spacing={4}>
                    {questions.map((question) => (
                        <Grid item key={question._id} xs={12} sm={6} md={4}>
                            <Card className={classes.card}>
                                <CardContent className={classes.cardContent}>
                                    <Box mb={2}>
                                        <Chip
                                            label={question.difficulty.toUpperCase()}
                                            className={classes.difficultyChip}
                                            size="small"
                                        />
                                        <Chip
                                            label={question.category.replace(/_/g, ' ').toUpperCase()}
                                            className={classes.categoryChip}
                                            size="small"
                                        />
                                    </Box>

                                    <Typography variant="h5" className={classes.cardTitle}>
                                        {question.title}
                                    </Typography>

                                    <Box className={classes.infoContainer}>
                                        <ReadingIcon className={classes.icon} />
                                        <Typography>
                                            {question.blanks.length} blanks to fill
                                        </Typography>
                                    </Box>

                                    <Box className={classes.infoContainer}>
                                        <SchoolIcon className={classes.icon} />
                                        <Typography>
                                            {question.points} points
                                        </Typography>
                                    </Box>

                                    <Box className={classes.infoContainer}>
                                        <TimerIcon className={classes.icon} />
                                        <Typography>
                                            Estimated time: {question.blanks.length * 2} minutes
                                        </Typography>
                                    </Box>
                                </CardContent>
                                
                                <CardActions style={{ padding: '16px' }}>
                                    <Button
                                        fullWidth
                                        className={classes.startButton}
                                        startIcon={<StartIcon />}
                                        onClick={() => handleStartQuiz(question.question_id)}
                                    >
                                        Start Practice
                                    </Button>
                                </CardActions>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            </Container>
        </Box>
    );
};

export default QuizList;
